
// 簡化的 PDF.js Worker 載入器
console.log('PDF Worker 初始化中...');

// 嘗試載入本地 PDF.js worker
const loadWorker = async () => {
  try {
    // 直接使用 CDN 版本作為備用
    const workerScript = 'https://cdnjs.cloudflare.com/ajax/libs/pdf.js/4.4.168/pdf.worker.min.js';
    importScripts(workerScript);
    console.log('✅ PDF Worker 載入成功');
  } catch (error) {
    console.warn('⚠️ Worker 載入失敗，使用備用方案');
    // 提供基本的 worker 功能
    self.onmessage = function(e) {
      self.postMessage({
        error: 'Worker 暫時不可用，請重試'
      });
    };
  }
};

loadWorker();
